import React, { useState } from "react";

const SurveyFlow = () => {
  const [currentStep, setCurrentStep] = useState(1);
  const [answers, setAnswers] = useState({});

  const handleNext = () => {
    setCurrentStep((prev) => prev + 1);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setAnswers({ ...answers, [name]: value });
  };

  return (
    <div className="p-6 bg-white shadow-lg rounded-lg max-w-lg mx-auto mt-10">
      {currentStep === 1 && (
        <div>
          <h2 className="text-2xl font-bold text-primary mb-4">
            Question 1: How many daily interactions do you expect?
          </h2>
          <select
            name="interactionVolume"
            onChange={handleInputChange}
            className="border border-gray-300 p-3 rounded w-full mb-4"
          >
            <option value="">Select...</option>
            <option value="0-100">0-100</option>
            <option value="101-500">101-500</option>
            <option value="501-1000">501-1000</option>
            <option value="1000+">1000+</option>
          </select>
          <button
            onClick={handleNext}
            className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-700 transition"
          >
            Next
          </button>
        </div>
      )}

      {currentStep === 2 && (
        <div>
          <h2 className="text-2xl font-bold text-primary mb-4">
            Question 2: Do you need after-hours support?
          </h2>
          <label className="flex items-center space-x-3 mb-4">
            <input
              type="radio"
              name="afterHoursSupport"
              value="yes"
              onChange={handleInputChange}
              className="h-5 w-5 text-primary focus:ring-primary"
            />
            <span>Yes</span>
          </label>
          <label className="flex items-center space-x-3 mb-4">
            <input
              type="radio"
              name="afterHoursSupport"
              value="no"
              onChange={handleInputChange}
              className="h-5 w-5 text-primary focus:ring-primary"
            />
            <span>No</span>
          </label>
          <button
            onClick={handleNext}
            className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-700 transition"
          >
            Next
          </button>
        </div>
      )}

      {currentStep > 2 && (
        <div>
          <h2 className="text-2xl font-bold text-primary mb-4">Thank you!</h2>
          <p>Your responses have been recorded.</p>
        </div>
      )}
    </div>
  );
};

export default SurveyFlow;
