import React from "react";
import Header from "../components/Header";
import Hero from "../components/Hero";
import Card from "../components/Card"; // Import the Card component

const Home = () => {
  return (
    <div className="bg-gradient-to-b from-highlight to-white min-h-screen">
      <Header />
      <Hero />
      <div className="py-10 px-5 grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
        {/* Example Cards */}
        <Card 
          title="AI-Powered Surveys" 
          description="Enhance your customer insights with dynamic AI-driven surveys." 
          link="/surveys" 
        />
        <Card 
          title="Document Management" 
          description="Organize and process business-critical documents effortlessly." 
          link="/documents" 
        />
        <Card 
          title="Automation Agents" 
          description="Deploy custom agents to automate your business tasks." 
          link="/agents" 
        />
      </div>
    </div>
  );
};

export default Home;
